INSERT INTO `campsite` (`campsite_id`, `name`, `description`, `address`, `city`, `zipcode`, `image`, `status`) VALUES
(1, 'Camping Les Cigales', 'Le Camping Les Cigales est un camping familial situé à proximité immédiate des calanques de Cassis. Avec un accès facile aux plages et aux sentiers de randonnée du Parc National des Calanques, il est l\'endroit idéal pour les amateurs de nature et d\'activités en plein air. Ce camping propose une ambiance conviviale et dispose de nombreuses commodités, dont une piscine, un restaurant, et des emplacements ombragés.', 'Avenue des Albizzi', 'Cassis', 13260, 'assets/img/campsites/cigales.png', 'Ouvert'),
(2, 'Camping de Ceyreste', 'Le Camping de Ceyreste est situé sur les hauteurs de La Ciotat, offrant des vues magnifiques sur la mer et un accès rapide aux calanques environnantes, telles que celles de Figuerolles et du Mugel. Ce camping dispose de nombreuses installations, comme une piscine, un terrain de sport et des espaces verts, et est particulièrement apprécié des familles et des randonneurs.', 'Avenue Eugène Julien', 'Ceyreste', 13600, 'assets/img/campsites/ceyreste.png', 'Ouvert'),
(3, 'Camping La Baie des Anges', 'Le Camping La Baie des Anges est un camping 4 étoiles situé à La Ciotat, à quelques minutes en voiture des célèbres calanques de la région. Le camping propose des emplacements spacieux, des hébergements modernes, ainsi qu\'un parc aquatique et des activités pour toute la famille. C\'est un excellent point de départ pour explorer les calanques de La Ciotat et celles plus à l\'ouest.', 'Chemin des Plaines Barronnes', 'La Ciotat', 13600, 'assets/img/campsites/baie-anges.png', 'Ouvert'),
(4, 'Camping Les Tamaris', 'Le Camping Les Tamaris est un charmant camping situé à La Ciotat, proche des plages et des calanques environnantes. Ce camping familial offre des emplacements confortables ainsi qu\'un cadre verdoyant, parfait pour ceux qui souhaitent explorer les richesses naturelles de la région tout en profitant d\'un séjour tranquille au bord de la mer.', 'Avenue de la Tramontane', 'La Ciotat', 13600, 'assets/img/campsites/tamaris.png', 'Ouvert'),
(5, 'Camping Les Flots Bleus', 'Le Camping Les Flots Bleus, situé à La Ciotat, est un camping familial proche des plages et des calanques. Ce camping est réputé pour son ambiance conviviale et sa proximité avec les calanques de Figuerolles et du Mugel. Les Flots Bleus offre des emplacements ombragés et des mobil-homes tout équipés, parfaits pour un séjour en pleine nature. Idéal pour les familles et les amateurs de sports nautiques, ce camping propose également diverses activités et services pour rendre le séjour agréable.', 'Avenue des Calanques', 'La Ciotat', 13600, 'assets/img/campsites/flot-bleu.png', 'Ouvert');

