DROP TABLE IF EXISTS `membership`;
CREATE TABLE IF NOT EXISTS `membership` (
  `card_id` int NOT NULL AUTO_INCREMENT,
  `delivery_date` datetime DEFAULT NULL,
  `expiry_date` datetime DEFAULT NULL,
