--
-- Structure de la table `trails`
--

DROP TABLE IF EXISTS `trails`;
CREATE TABLE IF NOT EXISTS `trails` (
  `trail_id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `difficulty` enum('Facile','Moyen','Difficile') COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `length_km` float DEFAULT NULL,
  `time` time DEFAULT NULL,
  `description` text COLLATE utf8mb4_unicode_ci,
  `status` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `image` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `infos` text COLLATE utf8mb4_unicode_ci,
  `acces` text COLLATE utf8mb4_unicode_ci,
  PRIMARY KEY (`trail_id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Déchargement des données de la table `trails`
--

INSERT INTO `trails` (`trail_id`, `name`, `difficulty`, `length_km`, `time`, `description`, `status`, `image`, `infos`, `acces`) VALUES
(1, 'Le tour de l’île de Ratonneau', 'Facile', 3.8, '01:15:00', 'Une boucle facile qui vous dévoilera un concentré des charmes de l\'archipel du Frioul. Nombreux vestiges archéologiques à découvrir. Le passage par la calanque de Saint-Estève est une invitation au farniente et à l\'exploration des petits fonds : n\'oubliez pas votre maillot de bain et votre masque de plongée !', NULL, 'assets/img/trails/tour-ratonneau.png', '1. Le départ s\'effectue depuis le quai où vous débarque la navette maritime. Prenez la route goudronnée dans la direction opposée au noyau villageois (vous y reviendrez à la fin de votre balade). Progressez jusqu\'à la plateforme agrémentée d\'un petit banc pour profiter d\'une belle vue sur Marseille.\n\n2. Reprenez votre parcours en cheminant sur le sentier longeant la calanque de Saint-Estève. Invitation à la baignade, la calanque de Saint-Estève est équipée d\'un sentier sous-marin en été. N\'hésitez pas à plonger la tête sous l\'eau et à suivre les bouées pour une visite guidée des petits fonds marins.\n\n3. Vous arrivez devant l\'hôpital Caroline : la visite du bâtiment n\'est pas possible mais vous pouvez en faire le tour pour admirer son architecture. Une fois le tour de l\'Hôpital effectué, rebroussez chemin en direction de la plage de la Calanque de Saint-Estève.\n\n4. À l\'embranchement, suivez le chemin s\'ouvrant sur votre droite en direction du fort de Ratonneau. En grimpant à travers le fort, essayez d\'identifier les différentes couches de son histoire : les pierres les plus anciennes datent du XVIe siècle et les plus récentes de l\'occupation allemande !\n\n5. Tournez en épingle sur la droite pour rejoindre le sommet du fort et ses étonnantes croix. Beau panorama sur l\'île de Ratonneau. Préparez-vous ensuite à la descente, notamment en empruntant une petite volée de marche. Cette partie du trajet permet d\'observer de nombreuses « forêts bonzaï » formées de pins d\'Alep poussant au ras du sol en raison de l\'action du vent.\n\n6. Rejoignez la route goudronnée que vous avez empruntée à l\'aller pour revenir à votre point de départ. En attendant la navette de retour, vous pouvez visiter le noyau villageois et ses petits commerces qui proposent des rafraîchissements !', 'Accès recommandé : Terminus de la navette Le bateau.'),
(2, 'Sur les traces d’Antoine Pellicé', 'Difficile', 4, '01:30:00', 'Partez sur les traces d’Antoine Pellicé, ancien Président des Excursionnistes Marseillais… Une randonnée courte mais qui grimpe bien ! De jolis points de vue sur le littoral sud de Marseille et vers le large.', 'active', 'assets/img/trails/sentier-president.png', 'Parcours\n1. Le départ s’effectue depuis l’arrêt de bus RTM Madrague de Montredon (ligne 19). Empruntez le chemin des Goudes sur 200 mètres et, avant la patte d’oie, tournez à gauche pour rejoindre le départ du sentier au double balisage noir / rouge et blanc qui grimpe rapidement dans les collines. Attention : quelques passages nécessitent de poser les mains.\n\n2. À la fourche, continuez sur le sentier noir / rouge et blanc, en ignorant les sentiers bleu et marron qui s’ouvrent sur votre droite. Progressez jusqu’au col de Brès où vous apprécierez la vue sur les Goudes, l’île Maïre et le large.\n\n3. Une fois passé le col de Brès, la descente sur les pentes du vallon de l’Agneau demande de progresser prudemment. Continuez votre progression sur le sentier au balisage noir / blanc et rouge, en ignorant les sentiers jaune et rouge que vous croiserez.\n\n4. Vous arrivez à la calanque de Callelongue : traversez le noyau villageois pour rejoindre le terminus de la ligne RTM 20 qui vous déposera à l’arrêt Madrague de Montredon, point de départ de la randonnée.', 'La Madrague-de-Montredon (8e arrondissement de Marseille). Accessible depuis le bus n°19.'),
(3, 'L’ascension du sommet de Béouveyre', 'Difficile', 4.6, '02:15:00', 'La superbe vue du sommet de Béouveyre se mérite ! Une randonnée avec une première partie de parcours marquée par un dénivelé important et quelques passages techniques.', NULL, 'assets/img/trails/sommet_de_beouveyre.png', '1. Le départ s’effectue depuis l’arrêt de bus RTM Madrague de Montredon (ligne 19). Empruntez le chemin des Goudes sur 200 mètres et, avant la patte d’oie, tournez à gauche pour rejoindre le départ du GR (balisage rouge et blanc) qui grimpe rapidement dans les collines.\n\n2. À l’embranchement, quittez le GR pour suivre le sentier marron en montant tout droit sur une centaine de mètres.\n\n3. À l’embranchement, continuez sur le chemin marron (le chemin bleu mène également au sommet mais la progression y est plus difficile). Attention : quelques passages nécessitent de poser les mains. Arrivé à un gros pierrier prendre le temps de repérer la balise marron et d’éviter le pierrier : des espèces fragiles protégées y poussent (la Sabline de Provence). Suivez le chemin jusqu’au sommet de Béouveyre.\n\n4. Profitez de la vue offerte depuis le sommet du Béouveyre avant de rebrousser chemin. Béouveyre signifie \"beau-voir\" en provençal. Ce sommet offre une magnifique vue panoramique sur l’Archipel de Riou, le Rocher de Saint-Michel, le rocher des Goudes, l’île Maïre, le cap Croisette, l’Archipel du Frioul et la Côte Bleue. De l’autre côté, vous pouvez observer le massif de l’Etoile, le massif du Garlaban, le mont Puget et le massif de Marseilleveyre.\n\n5. À l’embranchement, empruntez le sentier jaune qui redescend en direction du village des Goudes.\n\n6. À l’embranchement, prenez le GR à gauche.\n\n7. À l’embranchement, prenez le sentier rouge sur la droite jusqu’à arriver à l’entrée du village des Goudes.\n\n8. Rejoignez l’arrêt Les Goudes de la ligne 20 de la RTM.', 'La Madrague-de-Montredon (8e arrondissement de Marseille). Accessible depuis le bus n°19.'),
(4, 'Le tour du massif de Marseilleveyre', 'Difficile', 12, '05:00:00', 'Cette randonnée offre une grande diversité de paysages et de remarquables panoramas.', NULL, 'assets/img/trails/tour_massif_marseilleveyre.png', '1. Traverser la route pour prendre à gauche, devant le poste électrique, le sentier qui s’élève, atteint une croupe et longe une clôture. La quitter pour monter une zone rocheuse. Passer une cheminée d’évacuation et poursuivre par le sentier escarpé. Arrivé à une clôture de sécurité, grimper à droite sur le côté d’un éboulis, continuer à gauche par le sentier en dévers, descendre le ressaut rocheux, arriver près de la Roche Percée (située à gauche) et à un carrefour (jonction de tracés).\r\n\r\n2. Poursuivre (tracé noir). Le sentier tantôt en creux, tantôt sur des plaques rocheuses, traverse des parties boisées et mène dans le bois de la Selle.\r\n\r\n3. Monter (tracé vert) et atteindre, en quelques virages, le pas de la Selle.\r\n\r\n4. Le franchir, descendre à gauche (tracé vert) par le sentier rocailleux puis rocheux d’abord à flanc, puis au creux du vallon. Dépasser une vasque d’eau et gagner une intersection (tracé jaune). Continuer à droite par un agréable sentier (suite au feu de février 2013, l’itinéraire passe dans une forêt brûlée ; cette forêt est en train de se reconstituer, prendre bien soin de ne pas sortir du sentier) jusqu’à la calanque de Marseilleveyre.\r\n\r\n5. Prendre à droite (tracé noir et GR®) le chemin qui longe la mer, passer une petite habitation puis une ancienne batterie (à gauche). Le sentier devient plus rocheux, s’écarte de la côte, s’élève, domine la mer et visite le fond de la petite calanque de la Mounine. Poursuivre dans une zone rocheuse et arriver au petit port de Callelongue.\r\n\r\n6. Emprunter à droite l’avenue des Pébrons, bordée de cabanons, bifurquer à gauche, franchir la barrière et continuer le GR®. Le sentier en balcon contourne plusieurs vallons, puis franchit le pas Garrigue, avant d’atteindre le col de Brès.\r\n\r\n7. Poursuivre par le sentier qui franchit plusieurs ressauts rocheux aménagés et contourne encore quelques vallons. À une intersection (jonction de tracés), le sentier descend puis rejoint l’arrêt de bus.', 'La Madrague-de-Montredon (8e arrondissement de Marseille). Accessible depuis le bus n°19.'),
(5, 'De Pastré à Callelongue', 'Difficile', 6.5, '02:45:00', 'Une longue randonnée au départ du parc Pastré et à destination de Callelongue via le col de la Selle et le col de la Galinette.', NULL, 'assets/img/trails/pastre-callelongue.png', 'Parcours\n1. Le départ de la randonnée s’effectue depuis l’arrêt de bus Montredon – Pastré (ligne 19). Entrez dans le parc Pastré et remontez l’entrée principale. Au bout, prenez à gauche en direction du château Pastré et dépassez-le pour rejoindre l’entrée du Parc national des Calanques.\n\n2. Empruntez le sentier balisé en jaune qui s’enfonce dans le vallon des Trois Gancets. Vous poursuivrez le chemin jaune, jusqu’à votre arrivée à la calanque de Callelongue.\n\n3. En continuant votre progression sur le sentier jaune, soyez prudent : un passage délicat et escarpé se présente à vous. Continuez votre progression en direction du col de la Selle en profitant des panoramas sur la rade de Marseille et l\'archipel du Frioul.\n\n4. Rejoignez le col de la Galinette en profitant cette fois de vues sur le large, l’archipel de Riou et la calanque de Marseilleveyre.\n\n5. Une fois passé le col de la Galinette, le sentier amorce sa descente vers la calanque de Callelongue. Attention, descente abrupte sur les derniers mètres.\n\n6. Rejoignez le terminus de la ligne RTM 20 pour retourner en direction du centre-ville de Marseille.', 'La Madrague-de-Montredon (8e arrondissement de Marseille). Accessible depuis le bus n°19.'),
(6, 'La fontaine de Voire', 'Difficile', 7.5, '03:00:00', 'Cet itinéraire passe par la fontaine de Voire, lieu de légende, puis conduit au sommet Ouest de l’Homme Mort par des sentiers escarpés, dans un site sauvage, jusqu’à l’un des plus beaux points de vue des Calanques du massif de Marseilleveyre sur les calanques.', NULL, 'assets/img/trails/fontaine_de_voire.png', '1. 50 mètres après l’arrêt du bus, au rond-point de la Jarre, emprunter à droite le boulevard Pierotti jusqu’au bout de la route. Après une barrière, à la fourche, poursuivre à droite par la piste DFCI CQ 101 du vallon boisé de la Jarre (balisage rouge). Après une légère montée, franchir la barrière et arriver à la fontaine de Voire (aménagée dans une baume). S’engager dans de petites gorges et atteindre la clairière.\n\n2. Monter à gauche. Le sentier s’élève fortement sur un escarpement rocheux, contourne un mamelon et s’oriente au sud. Gravir le défilé rocheux et aboutir à une intersection, en corniche.\n\n3. Suivre le sentier à droite, gravir une zone rocheuse, continuer sur la crête escarpée, puis descendre au fond du vallon de l’Homme Mort (jonction avec le tracé vert).\n\n4. Couper le tracé vert et grimper dans une zone rocheuse. Après une succession de montées et descentes, aboutir à une intersection (jonction avec le tracé bleu du plateau de l’Homme Mort ; panorama sur la rade de Marseille et les îles).\n\n5. Tourner à droite, longer le plateau qui surplombe le Malvallon, atteindre le sommet ouest de l\'Homme Mort (374 mètres) et descendre un ressaut rocheux (prudence). Le sentier, balisé en bleu désormais, conduit au pas de la Selle.\n\n6. 20 mètres avant le col de la Selle, amorcer la descente à droite avec le tracé vert. Après quelques lacets, atteindre le bois de la Selle et un croisement (jonction des tracés jaune, vert et noir).\n\n7. Descendre (tracés jaune et rouge) et traverser la zone boisée. Poursuivre jusqu’à une fourche.\n\n8. Virer à droite (tracé rouge). Après un passage en corniche et une descente sur des plaques rocheuses, retrouver la clairière.', 'La Cayolle (9e arrondissement de Marseille). Accessible par le bus n°23 arrêt Jarre.'),
(7, 'Les monts de Luminy', 'Facile', 6, '02:00:00', 'Cette promenade sur de larges chemins offre un bon aperçu du massif des Calanques.', NULL, 'assets/img/trails/monts_de_luminy.png', '1. Se diriger vers le fond du parking, franchir la barrière et arriver à une fourche.\n\n2. Partir à droite en direction de Morgiou (vue sur le mont Puget à gauche). Rester sur la piste principale. Dans une agréable pinède, elle s’élève progressivement en lacets jusqu’à une intersection de trois pistes (citerne n° 67 ; panorama sur la ville de Marseille).\n\n3. Continuer la montée avec la piste centrale. Elle s’oriente au sud, dessine une grande courbe vers l’ouest, revient vers le sud et mène à une fourche.\n\n4. Poursuivre par le chemin de gauche. Il s’élève et, après un lacet, rejoint le sommet du mont Luminy (289 mètres ; vue sur les îles Plane, Riou et le massif du Puget). Le chemin redescend légèrement. Continuer sur cette voie, éviter le tracé jaune qui coupe les virages et remonter jusqu’à la crête des Escampons (286 m). Après un lacet, la piste parcourt la crête.\n\n5. Dans le virage, laisser les tracés jaune et vert et descendre la piste à gauche. Ignorer deux pistes à droite. Arriver sur une croupe, continuer à gauche sur le chemin principal en ignorant les autres chemins pour arriver à la citerne DFCI (Défense des Forêts Contre l’Incendie) 282.\n\n6. Descendre à droite dans le vallon des Trois-Gonfronts. Rester sur la voie principale. Elle remonte à gauche et ramène à la fourche de l’aller.\n\n7. Regagner le point de départ.', 'Luminy à 10 km au sud-est du centre-ville de Marseille. Accessible par le bus B1 – direction Campus de Luminy, arrêt Luminy – PN des Calanques.'),
(8, 'Le tour du mont Puget', 'Difficile', 12, '04:30:00', 'Cette randonnée vous entraîne sur les hauteurs de l’imposant massif du Puget qui domine le domaine de Luminy et offre des points de vue sur l’arrière-pays.', NULL, 'assets/img/trails/tour_mont_puget.png', '1. Se diriger vers le sud pour franchir la barrière DFCI CQ 109 d’accès au massif et emprunter le large chemin qui longe les bâtiments de l’université (panneaux d’information sur le massif des Calanques) et continue dans une belle pinède vallonnée. \n\n 2. Laisser le sentier qui part à droite (jaune pointillé) vers la calanque de Morgiou et continuer tout droit par la piste jusqu’à l’intersection de chemin du col de Sugiton (citerne DFCI N°65). \n\n 3. Monter par la piste à gauche sur 400m (balisage bleu). Marchez jusqu’à arriver à la borne DFCI CQ202, puis suivre le balisage pointillé rouge qui monte à droite (chemin du Centaure). Continuez sur la piste pointillé rouge jusqu’à ce qu’il redescende et rencontre un tracé rouge. \n\n 4. Continuer la descente (tracé rouge). Le tracé quitte la piste pour passer à droite, dans un sentier qui s’engage dans un vallon et mène au puits de Cancel (jonction des tracés bleu, vert et vert pointillé). \n\n 5. Face à un rocher, partir à gauche (balisage rouge, vert et bleu). Le sentier caillouteux remonte le vallon de l’Herbe et pénètre dans de petites gorges rocheuses. La végétation devient plus rare, puis le vallon s’élargit. Arriver à une fourche. \n\n 6. Bifurquer sur le sentier à droite (balisage vert). Il monte à flanc en balcon, quitte le vallon de l’Herbe et s’oriente vers la mer. À la jonction avec le tracé vert pointillé qui vient du vallon de Sainte Marthe, continuer à gauche et arriver à une intersection (jonction des tracés vert et jaune ; direction col de la Candelle). \n\n 7. Suivre à droite (tracés vert, blanc-rouge et jaune) par le sentier de crête, rocheux et escarpé, jusqu’au col de la Candelle. \n\n 8. Prendre à droite le sentier des « treize contours ». Il descend en balcon, parfois brusquement, contourne plusieurs vallons et arrive au « grand éboulis ». Le descendre (prudence) en restant le plus près possible de la partie rocheuse. En bas de l’éboulis, continuer par le sentier et aboutir sur une piste. \n\n 9. La suivre à droite (tracés vert et jaune) et retrouver le col de Sugiton. Par l’itinéraire de l’aller, regagner le point de départ.', 'Luminy à 10 km au sud-est du centre-ville de Marseille. Accessible par le bus B1 – direction Campus de Luminy, arrêt Luminy – PN des Calanques.'),
(9, 'Le tour du belvédère d’En-Vau', 'Difficile', 15, '03:45:00', 'Cette randonnée emprunte le vallon d’En-Vau pour atteindre un belvédère naturel qui surplombe la calanque.', NULL, 'assets/img/trails/tour_belvedere_en_vau.png', '1. Suivre la route Gaston Rébuffat sur 300 mètres. Bifurquer à gauche, puis prendre le sentier qui descend à droite assez brusquement. Emprunter la route à gauche, dépasser le mémorial Gaston Rébuffat (1921-1981), pénétrer dans le domaine de Gardiole et, 50 mètres après, monter à gauche. Le sentier coupe la route deux fois avant d’atteindre le site de la Gardiole. \n\n 2. Continuer par la route et utiliser à droite le sentier qui évite les virages. Avant la maison forestière, partir à droite sur la voie qui la contourne. Au carrefour, poursuivre par la piste à droite (tracés brun et rouge). Elle descend en lacets dans le vallon d’En-Vau.\n\n3. Au croisement (séparation des tracés), suivre la piste à droite sur 20 mètres, puis grimper à gauche par le sentier rocheux qui mène sur un plateau. Rester sur le chemin jusqu’au col de l’Oule (citerne 243 ; jonction avec les tracés bleu, vert et le GR®).\n\n4. Emprunter le chemin (bleu) qui s’oriente au sud (points de vue) puis atteint le belvédère naturel qui domine la calanque d’En-Vau (la vue sur la calanque bordée de hautes falaises est unique).\n\n5. Par le même itinéraire, revenir au col de l’Oule. Descendre à droite de la citerne (GR® et tracé vert) par le chemin rocailleux qui aboutit dans le vallon d’En-Vau. Suivre à droite ce vallon encaissé et bordé de végétation.\n\n6. Attention travaux : suivre la déviation en empruntant la piste en direction de l\'auberge de la Fontasse. Au croisement avec le sentier pointillé rouge, prendre à droite. Au prochain croisement, prendre le sentier bleu à droite. Si vous souhaitez découvrir la Calanque d’En-Vau, poursuivre sur le sentier rouge à droite (Comptez 30 minutes supplémentaires et une trentaine de mètre de dénivelé en plus), remonter le vallon puis suivre la déviation.\n\n7. Emprunter le chemin bleu à gauche (vue remarquable sur la rade de Cassis et les falaises Soubeyranes). Passer le domaine de Fontasse et poursuivre sur 300 mètres.\n\n8. Prendre à gauche le chemin qui évite les virages de la piste, puis continuer à gauche par la piste jusqu’au col de la Gardiole.', 'Se garer au parking du Logisson, à 16 km au sud-est du centre-ville de Marseille par la D 559 (route de la Gineste, direction Cassis).'),
(10, 'Le plateau d\'En-Vau', 'Moyen', 8, '03:00:00', 'Après les calanques de Port-Miou et Port-Pin, le sentier s’élève sur le plateau qui surplombe la calanque d’En-Vau.', NULL, 'assets/img/trails/plateau-envau.png', '1. Suivre le sentier parallèle à l’avenue Notre-Dame, qui descend à gauche en direction du centre-ville (nord-est). En face du n°15, descendre à gauche (balisage vert).\n\n2. Emprunter l’avenue des Calanques à gauche sur 200 mètres, puis tourner à gauche sur le chemin qui longe l’ancienne carrière Solvay (carrière active entre 1900 et 1981 pour exploiter le calcaire utilisé dans la fabrication de la chaux) et monter sur le plateau. Juste après un virage, s’engager à gauche sur le sentier de crête qui descend vers la presqu’île Cacau, puis utiliser le sentier aménagé qui descend à droite dans la calanque de port-Pin.\n\n3. Au fond de la petite calanque, prendre le sentier à gauche (balisé en bleu). Il longe puis domine la calanque jusqu’à la pointe d’En-Vau. Reprendre la montée, passer devant le refuge du Piolet (échappées sur les falaises abruptes d’En-Vau) et poursuivre par le sentier bien aménagé qui s’oriente progressivement nord/nord-ouest pour longer le plateau (panorama sur la mer, les îles, la Grande Candelle). Le sentier s’oriente à l’est, devient chemin. À une fourche, descendre à gauche pour atteindre une intersection.\n\n4. Descendre à droite par le GR® (balisage blanc-rouge, tracés vert et bleu) vers la calanque de port-Pin. Le sentier, par endroits, rocheux, traverse une végétation rase (la vue s’étend sur la rade de Cassis). Continuer la descente et retrouver le fond de la calanque de Port-Pin.\n\n5. Par l’itinéraire de l’aller, regagner le point de départ.', 'Cassis, presqu’île de Port-Miou, au sud-ouest de la ville. Accessible après le terminus de la navette.'),
(11, 'Le sentier du Petit Prince', 'Facile', 1.7, '00:00:30', 'Une boucle très facile autour de la presqu’île de Cassis qui offre de superbes vues sur la calanque de Port-Miou et sur le cap Canaille. Le nom du sentier est un hommage à Antoine de Saint-Exupéry, dont on a longtemps cru que l’avion s’était échoué au large de Cassis.', NULL, 'assets/img/trails/sentier_petit_prince.png', '1. Le départ de la balade se situe au croisement de l’avenue Notre-Dame et de l’avenue de la Plage Bleue, là où se trouve le panneau d’accueil du Parc national des Calanques. Le cheminement se suit très facilement, notamment grâce à des plaques émaillées qui permettent d’en savoir plus sur les Calanques et l’histoire des lieux.\r\n\r\n2. Le chemin se divise en deux branches, suivez celle de droite pour faire une petite boucle et revenir à l’embranchement.\r\n\r\n3. Prenez à droite pour explorer le chemin que vous aviez ignoré plus tôt. Passage devant Notre-Dame de Bon Voyage. \r\n\r\n4. Une fois arrivé sur le petit parking, longez le tennis en profitant de la vue sur le cap Canaille et, si vous le souhaitez, des grandes dalles rocheuses de la Plage Bleue.\r\n\r\n5. Prenez la route menant au parking boisé de la presqu’île. \r\n\r\n6. Prenez le petit chemin qui monte sur votre gauche et tournez à droite quand vous l’aurez remonté pour retourner au point de départ de la balade.', 'Arrêt Presqu’île de la navette Parking-relais des Gorguettes en direction des Calanques.'),
(12, 'Le tour du sémaphore du bec de l\'Aigle', 'Moyen', 8, '02:40:00', 'Le sémaphore du bec de l’Aigle domine La Ciotat et l’île Verte. Une belle balade sur les hauteurs de la ville.', NULL, 'assets/img/trails/tour_semaphore_bec_aigle.png', '1. Emprunter l’avenue du Mugel, puis suivre à droite l’avenue de Figuerolles et à gauche le chemin de la Garde jusqu’à l’intersection des deux routes.\n\nHors itinéraire vers Notre-Dame de la Garde (20 min aller et retour) : suivre à gauche la traverse de la Garde et le chemin aménagé qui conduit à la chapelle.\n\n2. Continuer tout droit le chemin du Sémaphore qui monte progressivement. Rester à droite lorsque la route s’écarte à gauche. Continuer 100 mètres.\n\n3. Deux chemins sont visibles, suivre le balisage jaune et prendre le chemin qui monte à droite. Devant un panneau « propriété privée », prendre à gauche le sentier en balcon qui longe un muret de pierres, puis des barres rocheuses. Suivre le tracé jaune en direction du sémaphore en ignorant les autres sentiers. Passer devant le sémaphore, suivre à droite le sentier qui le contourne pour arriver à la route des Crêtes. La traverser pour accéder à la table d’orientation. Longer la route puis la traverser.\n\n4. Se diriger à droite vers « La Ciotat par la chapelle Sainte-Croix ». Rester sur la crête (tracé jaune). Elle s’oriente progressivement vers le sud-est. Longer des murs de pierres pour aboutir à la chapelle Sainte-Croix (ruines).\n\n5. Descendre le chemin de Sainte-Croix, traverser une avenue pour prendre les escaliers. Suivre l’avenue de la Pétanque (cimetière à droite) puis l’avenue Galliéni, la place des Esquiros et la rue Gueymard à droite. Longer à droite le Vieux-Port vers les Calanques et le point de départ.', 'La Ciotat, à 26 km à l’est de Marseille par l’A50. Accessible en train puis en bus.'),
(13, 'Le grand tour du Mugel', 'Facile', 2.4, '01:00:00', 'Une randonnée qui ne présente pas de difficulté majeure et qui vous fera découvrir tous les charmes du Mugel et du bec de l’Aigle. Criques enchanteresses, jardin extraordinaire et belvédère offrant des vues à couper le souffle : tout y est ! Attention cependant, le littoral est surfréquenté l\'été. Les amateurs de calme passeront leur tour ou reviendront hors saison.', NULL, 'assets/img/trails/grand_tour_du_mugel.png', '1. Le départ de la randonnée s’effectue depuis le parking du Mugel. Rejoignez l’entrée du parc botanique du Mugel en longeant la plage du Grand Mugel. \r\n    2. Gardez votre gauche en entrant dans le parc du Mugel, de façon à longer le littoral. Au bout du chemin, admirez la vue sur l’anse du Sec puis retournez sur vos pas pour vous diriger en direction de la maison abandonnée qui surplombe l’anse du Sec. \r\n    3. Prenez à gauche pour profiter d’une nouvelle vue impressionnante sur l’anse du Sec. Progressez prudemment. L’accès au belvédère de l’anse du Sec n’est pas conseillé aux personnes souffrant de vertige. Retournez sur vos pas pour reprendre votre progression dans le sens de la montée. \r\n    4. Prenez à gauche pour aller voir l’impluvium du Mugel de plus près, puis retournez sur vos pas. \r\n    5. Reprenez votre progression en direction du belvédère du bec de l’Aigle. La montée est courte mais le sentier grimpe rapidement, vous serez néanmoins récompensé par la vue ! Redescendez ensuite par le chemin par lequel vous êtes arrivé. \r\n    6. Tournez à gauche et dirigez-vous vers la sortie du parc du Mugel. N’hésitez pas à flâner et à vous perdre dans les allées de ce jardin à la végétation luxuriante. \r\n    7. Une fois la sortie du parc du Mugel atteinte, le retour s’effectue par le même chemin qu’à l’aller.', 'Parking du Mugel. En période estivale, le parking et les alentours sont saturés : rendez-vous au début de l\'itinéraire à pied ou en transports.');

-- --------------------------------------------------------

--
-- Structure de la table `users`
--

DROP TABLE IF EXISTS `users`;
CREATE TABLE IF NOT EXISTS `users` (
  `user_id` int NOT NULL AUTO_INCREMENT,
  `role` varchar(50) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `lastname` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `firstname` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `mail` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `password` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `phone` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `address` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `city` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `zipcode` int DEFAULT NULL,
  `registration_date` datetime DEFAULT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- --------------------------------------------------------

--
-- Structure de la table `users_icons`
--

DROP TABLE IF EXISTS `users_icons`;
CREATE TABLE IF NOT EXISTS `users_icons` (
  `user_id` int NOT NULL,
  `icon_id` int NOT NULL,
  PRIMARY KEY (`user_id`,`icon_id`),
  KEY `icon_id` (`icon_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- --------------------------------------------------------

--
-- Structure de la table `users_trails`
--

DROP TABLE IF EXISTS `users_trails`;
CREATE TABLE IF NOT EXISTS `users_trails` (
  `user_id` int NOT NULL,
  `trail_id` int NOT NULL,
  `trail_done` tinyint(1) DEFAULT NULL,
  PRIMARY KEY (`user_id`,`trail_id`),
  KEY `trail_id` (`trail_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

